import Foundation

class ClassTemplate {
    
    static func getTemplate() -> String {
        let template: String = """
// WARNING: THIS FILE IS AUTOGENERATED
//          BY ChickGen üê•
// MORE INFO: github: cpageler93/ChickGen
//
//
//  {{ fileName }}
//  {{ projectName }}
//
//  Created by {{ author }} on {{ date }}
//
//

{% for import in imports %}
import {{ import }}{% endfor %}


{{class.accessControl}} class {{ class.name }} {
{% for attr in class.attributes %}
    {{attr.accessControl}} {{ attr.ref }} {{ attr.name }}: {{ attr.type }} {{ attr.defaultValue }}{% endfor %}
{% for func in class.functions %}
    {{func.accessControl}} func {{ func.name }}({{ func.formattedParameters }}) {{ func.formattedReturn }} {
{% for bodyLine in func.bodyLines %}
        {{ bodyLine }}{% endfor %}

    }
{% endfor %}
}
"""
        return template
    }
    
}
