import Foundation

class EnumTemplate {
    
    static func getTemplate() -> String {
        let template: String = """
// WARNING: THIS FILE IS AUTOGENERATED
//          BY ChickGen üê•
// MORE INFO: github: cpageler93/ChickGen
//
//
//  {{ fileName }}
//  {{ projectName }}
//
//  Created by {{ author }} on {{ date }}
